        color: var(--theme_default_text) !important;
        transition: .1s;
    }
    .button-mob:hover {
        background-color: var(--theme_default_btnbghover);
    }
    .button-mob[href="#"]:first-child {
        display: none !important;
    }

    .thread-nav-mob {
        height: 0;
    }
    .button-mob[href$="/catalog.html"] {
        background: none;
        box-shadow: none;
        font-size: 0;
        padding: 0 !important;
    }
    .button-mob[href$="/catalog.html"]::before {
        color: var(--theme_default_alttext);
        content: "apps";
        font: 24px/50px 'Material Icons', sans-serif;
        position: fixed;
        bottom: 0;
        left: 60%;
        width: 20%;
        z-index: 979;
    }
    .button-mob[href$="/catalog.html"]:hover::before {
        background: var(--theme_default_btnbghover);
    }

    .thread-nav {
        display: block !important;
        font-size: 0;
    }
    .search {
        background: var(--theme_default_postbg);
        border-radius: 8px;
        box-shadow: 0 0 0 1px var(--theme_default_border), 0 -1px 6px 0 var(--theme_shadow2);
        display: block !important;
        margin-right: 0;
        padding: 9px;
        position: fixed;
        bottom: 60px;
        left: -100%;
        height: 50px;
        transition: .3s;
        z-index: 970;
    }
    .replypage .search {
        bottom: 100px;
    }
    .search:hover,
    .search:focus,
    .search:focus-within {
        left: 2%;
    }
    .search::after {
        color: var(--theme_default_alttext);
        content: "search";
        cursor: pointer;
        font: 24px/50px 'Material Icons', sans-serif;
        position: fixed;
        bottom: 0;
        left: 20%;
        width: 20%;
        text-align: center;
        transition: .1s;
    }
    .search:hover::after,
    .search:focus::after,
    .search:focus-within::after {
        background: var(--theme_default_btnbghover);
    }
    input[name="find"] {
        font-size: 15px;
        padding: 6px 10px;
    }
    .thread-nav__inner a {
        display: none;
    }

    .thread-nav__stats {
        font-size: 15px;
        position: fixed;
        height: 40px;
        bottom: 50px;
        right: 0;
        padding: 10px 15px;
        z-index: 969;
    }
    a[id="bottom"] + .thread-nav,
    .button-mob[href="#bottom"],
    .button-mob[href="#top"],
    .thread-nav-mob:nth-of-type(4) > a.button-mob:nth-of-type(4),
    a[id="bottom"] + * + * > a[onclick*="updateThread(); return false;"] {
        display: none !important;
    }

    a[onclick*="updateThread(); return false;"] {
        background: none;
        border-radius: 0;
        box-shadow: none;
        color: var(--theme_default_text);
        font-size: 0;
        padding: 0 !important;
        position: fixed;
        left: 0;
        bottom: 50px;
        z-index: 977;
        margin: 0;
        height: 40px;
        width: 15%;
        text-align: center !important;
    }
    a[onclick*="updateThread(); return false;"]::before {
        display: none;
    }
    a[onclick*="updateThread(); return false;"]::after {
        background: none !important;
        color: var(--theme_default_alttext2) !important;
        content: "refresh";
        font: 24px/40px 'Material Icons', sans-serif;
        position: unset;
        padding: 0;
    }
    a[onclick*="updateThread(); return false;"]:hover {
        background-color: var(--theme_default_btnbghover);
    }

    .autorefresh {
        background: var(--theme_default_postbg);
        box-shadow: 0 0 0 1px var(--theme_default_border), 0 -1px 6px 0 var(--theme_shadow2);
        font-size: 0;
        display: block !important;
        padding: 10px;
        padding-left: 15%;
        position: fixed;
        bottom: 50px;
        height: 40px;
        left: 0;
        right: 0;
        z-index: 969;
    }
    /*.autorefresh::before {background: var(--theme_default_postbg); box-shadow: 0 0 0 1px var(--theme_default_border), 0 -1px 6px 0 var(--theme_shadow2); content: ""; position: fixed; height: 35px; bottom: 55px; left: 0; right: 0;}*/
    .autorefresh-countdown {
        font-size: 14px;
        margin-left: 4px;
        vertical-align: -3px;
    }
    .thread-nav span {
        color: var(--theme_default_alttext)
    }

    /* Thread */
    .thread {
        margin-bottom: 16px;
    }
    .thread__oppost {
        margin-bottom: 0;
    }
    .thread__post {
        padding: 0;
        margin: 0;
    }
    .replypage .thread__post {
        margin: 0;
    }

    .post {
        padding: 0 0 6px;
    }
    .post_type_oppost {
        border-radius: 0;
        box-shadow: 0 0 0 1px var(--theme_default_border);
        display: block;
        min-width: unset;
        width: 100%;
    }
    .replypage .post_type_oppost {
        margin-bottom: 0;
    }
    .post__message {
        margin: 1em;
    }
    .oppost-options-mob {
        padding: 6px 8px;
    }

    .post_type_reply {
        border-radius: 0;
        box-shadow: 0 0 0 1px var(--theme_default_border);
    }
    .post_type_highlight {
        box-shadow: inset 0 0 0 1px var(--theme_default_borderhighlight);
    }

    .post_type_watched,
    .post_type_replied {
        margin-left: 0;
    }
    .post_type_watched:before,
    .post_type_replied:before {
        left: -1px;
    }

    .post__details,
    .post .post-details {
        background: none;
        padding: 4px 8px;
    }
    .post__ophui {
        padding: 0 2px;
    }
    .post__btn.desktop {
        display: inline-block !important;
    }
    .post__detailpart {
        padding-left: 2px;
    }
    .post__number {
        margin-left: 8px;
    }

    .modal {
        padding: 6px 0;
        left: unset !important;
        right: 10px;
    }
    .modal a {
        font-size: 15px;
        padding: 6px 14px;
    }

    .post__filezise {
        color: var(--theme_default_alttext) !important;
        margin-top: 0;
        visibility: visible !important;
    }
    .post__image-link {
        float: none;
    }
    .post__images_type_single + .post__message {
        margin: 0 1em 0.6em;
    }

    /* Footer */
    .bb {
        border-radius: 8px;
        box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2);
        display: block !important;
        font-size: 1.1em;
        top: unset;
        left: calc(-100vw);
        bottom: 60px;
        width: calc(100vw - 4%);
        transition: .2s;
        z-index: 979;
    }
    .replypage .bb {
        bottom: 100px;
    }
    .bb:hover,
    .bb:focus,
    .bb:focus-within {
        left: 2%;
    }
    .bb__panels {
        display: block !important;
    }
    .bb__header,
    .bb-header-text {
        font-weight: bold;
        font-size: 18px;
    }
    .bb__update,
    .fav__fa_type_remove,
    .fav__fa_type_update {
        width: 20px;
        height: 20px;
    }
    .bb__header::before {
        color: var(--theme_default_alttext);
        content: "star";
        font: 24px/50px 'Material Icons', sans-serif;
        position: fixed;
        left: 40%;
        bottom: 0;
        height: 50px;
        width: 20%;
        text-align: center;
        transition: .1s;
    }
    .bb__header:hover::before {
        background-color: var(--theme_default_btnbghover);
    }
    .bb__footer {
        font-size: 14px;
        padding: 0 8px;
        text-align: left;
    }
    .bb__footer label {
        display: block;
        margin: 4px 0;
    }
    .bb__tabs {
        font-size: 1.1em;
    }
    .bb__bstitle {
        max-width: 250px;
    }

    .footer__info {
        display: none;
    }

    /* ===== Dollchan ===== */
    #de-panel {
        bottom: 56px;
    }

    .de-parea-btn-thr,
    .de-parea-btn-close,
    .de-parea-btn-reply {
        border-radius: 50%;
        bottom: 10px;
        right: 20px;
        position: fixed;
        width: 50px;
        z-index: 981;
    }
    .de-parea-btn-thr::after,
    .de-parea-btn-reply::after {
        content: "create";
        font: 24px/40px 'Material Icons', sans-serif;
    }
    .de-parea-btn-close::after {
        content: "close";
        font: 24px/40px 'Material Icons', sans-serif;
    }

    #de-pform {
        background: var(--theme_default_postbg);
        left: 0;
        right: 0;
        top: 0;
        bottom: unset;
        position: fixed;
        z-index: 980;
    }
    #de-pform > form {
        padding: 0 5px 125px;
    }

    .de-runned a[onclick*="updateThread(); return false;"] {
        display: none !important;
    }
    .replypage .de-thr-updater-link::before {
        color: var(--theme_default_text);
        font: 24px/55px 'Material Icons', sans-serif;
        height: 55px;
        width: 20%;
        top: unset;
        bottom: 0;
        left: 60%;
        right: unset;
        z-index: 977;
        text-align: center;
    }
    .replypage .de-thr-updater-link:hover::before {
        background: var(--theme_default_btnbghover);
        color: var(--theme_default_text);
    }

    .de-mypost {
        margin-left: 0;
    }
    .de-mypost::before {
        left: 0;
    }
}

/* Hide subreddits top bar */

.sr-list > .separator,
.sr-list > .separator + .sr-bar,
#sr-more-link {
	display: none !important;
}
#sr-header-area {
	height: 0 !important;
}
.sr-bar,
.dropdown.srdrop {
	float: right !important;
	margin-left: 1em;
}
/* Hide annoying stuff */
.give-gold-button,
.goldvertisement,
.embed-comment,
.nub {
	display: none !important;
}
/* Reset */
*, *:before, *:after {
	background-color: transparent !important;
	border: none !important;
	color: #777 !important;
}
/* Default links */
a {
	color: #48b !important;
}
a:visited {
	color: #369 !important;
}
a:hover {
	color: #5ad !important;
}
/* Background */
html,
body,
#sr-more-link {
	background-color: #222 !important;
}
/* Top bar */
#header-img,
