@description    `Simple flat dark grey theme for Reddit. <br>Recommend disabling subreddit themes in reddit preferences.`
@version        20181014.1.34
@license        CC0-1.0
@preprocessor   uso
@advanced dropdown sr-top-bar "Hide subreddits top bar" {
	show "Show*" <<<EOT  EOT;
hide "Hide" <<<EOT .sr-list > .separator,
.sr-list > .separator + .sr-bar,
#sr-more-link {
	display: none !important;
}
#sr-header-area {
	height: 0 !important;
}
.sr-bar,
.dropdown.srdrop {
	float: right !important;
	margin-left: 1em;
} EOT;

}

