:root {
--clr_link: 205;
--clr_link2: 100%;
--clr_link3: 0%;
--clr_num: 120,40%;
--clr: 205;
--clr2: 0%;
--theme_scrollbg: hsl(var(--clr),var(--clr2),93%,.2);
--theme_scroll: hsl(var(--clr),var(--clr2),70%);
--theme_scrollhover: hsl(var(--clr),var(--clr2),65%);
--theme_scrollactive: hsl(var(--clr),var(--clr2),60%);
--theme_default_bg: hsl(var(--clr),var(--clr2),93%);
--theme_default_postbg: hsl(var(--clr),var(--clr2),100%);
--theme_default_postbg2: hsl(var(--clr),var(--clr2),93%);
--theme_default_postbg3: hsl(var(--clr),var(--clr2),95%);
--theme_default_glassbg: hsl(var(--clr),var(--clr2),100%,.7);
--theme_default_glassbg2: hsl(var(--clr),var(--clr2),100%,.9);
--theme_default_borderhighlight: var(--theme_default_link);
--theme_default_shadowhighlight: hsl(var(--clr_link),var(--clr_link2),40%,.6);
--theme_default_postbghighlight: hsl(var(--clr_link),calc(var(--clr_link2)/2),95%);
--theme_default_mod: hsl(var(--clr_link),var(--clr_link2),30%);
--theme_default_spectext: hsl(290,30%,45%);
--theme_default_quote: hsl(var(--clr_num),40%);
--theme_default_quote2: hsl(var(--clr_num),45%);
--theme_default_quote-bg: hsl(var(--clr_num),40%,.1);
--theme_default_border: hsl(var(--clr),var(--clr2),0%,.15);
--theme_default_altborder: hsl(var(--clr),var(--clr2),0%,.2);
--theme_default_text: hsl(var(--clr),var(--clr2),20%);
--theme_default_alttext: hsl(var(--clr),var(--clr2),40%);
--theme_default_alttext2: hsl(var(--clr),var(--clr2),60%);
--theme_default_contrasttext: hsl(var(--clr),var(--clr2),100%);
--theme_red: hsl(0,100%,60%);
--theme_yellow: hsl(45,100%,60%);
--theme_green: hsl(120,70%,60%);
--theme_sage: hsl(0,100%,70%);
--theme_ophui: hsl(var(--clr_num),40%);
--theme_default_link: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 40%));
--theme_default_hover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 30%));
--theme_default_altlink: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 40%),.8);
--theme_default_althover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 30%),.8);
--theme_default_title: hsl(var(--clr_link),var(--clr_link2),20%);
--theme_default_drag: hsl(var(--clr),var(--clr2),0%,.05);
--theme_default_spoiler: hsl(var(--clr),var(--clr2),80%);
--theme_default_icon: hsl(var(--clr),var(--clr2),60%);
--theme_default_btntext: var(--theme_default_text);
--theme_default_altbtntext: hsl(var(--clr),var(--clr2),100%);
--theme_default_btnbg: hsl(var(--clr),var(--clr2),100%);
--theme_default_btnborder: hsl(var(--clr),var(--clr2),0%,.15);
--theme_default_altbtnbg: hsl(var(--clr),var(--clr2),97%);
--theme_default_btnbghover: hsl(var(--clr),var(--clr2),97%);
--theme_default_altbtnbghover: hsl(var(--clr),var(--clr2),100%);
--theme_default_inputtext: var(--theme_default_text);
--theme_logo_text: var(--theme_default_link);
--theme_select: 100% 9px;
--theme_checkbox: 1px 2px;
--theme_nmswitch: hsl(var(--clr),var(--clr2),75%);
--theme_mpborder: hsl(var(--clr),var(--clr2),85%);
--theme_mubtn: transparent;
--theme_sbthumb: var(--theme_scroll);
--theme_sbtrack: var(--theme_scrollbg);
--theme_invert: unset;
--theme_shadow: hsl(var(--clr),var(--clr2),2%,.05);
--theme_shadow2: hsl(var(--clr),var(--clr2),2%,.1);
--theme_shadow3: hsl(var(--clr),var(--clr2),2%,.15);
--theme_shadow4: hsl(var(--clr),var(--clr2),2%,.25);
--theme_blockbg: var(--theme_default_postbg);
--theme_border: var(--theme_default_border);
}

