/* ===== Page ===== */
.warningcover {background-color: var(--theme_default_glassbg2); backdrop-filter: blur(10px); opacity: 1;}
.warningbox {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_shadow2); margin-top: -50px; overflow: auto;}
.warningboxbutton a {font-weight: bold; text-transform: uppercase; letter-spacing: .5px;}
.warningboxbutton a[id="ageboxallow"] {font-size: 0;}
.warningboxbutton a + br {display: none;}
.warningboxbutton a[id="ageboxallow"]::before {content: "Я согласен"; font-size: 18px; margin-right: 12px;}
.warningboxbutton a[id="ageboxdisallow"] {color: var(--theme_red);}

#fullscreen-container {animation: open .1s ease-out; box-shadow: 0 6px 16px var(--theme_shadow4); padding: 0; margin: 6px 4px 4px 4px;}
#fullscreen-container:hover:after {opacity: 1;}
#fullscreen-container:after {background: var(--theme_default_glassbg); border-radius: 3px; color: var(--theme_default_text); padding: 1px 5px; top: 2px; left: 2px; width: unset; opacity: 0; transition: .1s; white-space: unset;}
