select {background: var(--theme_default_altbtnbg); background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="18" height="44"><defs><g><path id="select" d="M10.3 0L5.5 4.7.7 0 0 .7 5.5 6 11 .7z"/></g></defs><use href="%23select" x="0" fill="%23000"/><use href="%23select" x="0" y="22" fill="%23fff"/></svg>') !important; background-position: var(--theme_select) !important; background-repeat: no-repeat !important; border: 1px solid var(--theme_default_border); border-radius: 3px; color: var(--theme_default_text); padding: 2px 22px 2px 6px !important; outline: none; transition: border .1s, background .1s, color .1s; -webkit-appearance: none; -moz-appearance: none; appearance: none;}
