/* ==UserStyle==
@name           Dark and fix width for Rutor
@namespace      USO Archive
@author         Евгений Лаптев
@description    `Темная тема фиксированной ширины для Rutor и его зеркал. Без рекламы.`
@version        20181216.0.5
@license        CC-BY-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("rutor.info"), domain("rutor.is"), domain("mirror-rutor.org"), domain("zerkalo-rutor.org"), domain("ru-free-tor.org"), domain("open-tor.org"), domain("fast-bit.org"), domain("ru-tor.net"), domain("fast-tor.net"), domain("proxy-rutor.org"), domain("new-tor.org"), domain("free-ru.org"), domain("rus-tor.com") {
html, body {
    font-size: 12.5 !important;
    background: #2e2e2e !important;
    max-width: 1100px !important;
    width: 95%;
    margin-left: auto !important;
    margin-right: auto !important;
}
#all {
    margin: 4px !important;
}
.news_date {
    background-color: #464646 !important;
}
a {
    color: #fedd00 !important;
}
.news_title a {
    color: #d0d0d0 !important;
}
.news_title a:hover, b:hover {
    color: #ffe000 !important;
}
#menu {
    width: 100% !important;
    height: 30px !important;
    background-image: none !important;
}
#flash_header, div.sideblock2, div#MarketGid5190, #logo_mediaget_box, #mediaget_box, a#toolbar, a.rss, .b_tz_on_top {
    display: none !important;
}
.ui-datepicker table {
    background: #3a3a3a !important;
}
.ui-state-default, .ui-widget-content .ui-state-default {
    border: 1px solid #656565 !important;
    background: none !important;
}
.ui-state-highlight, .ui-widget-content .ui-state-highlight {
    border: 1px solid #ffde00 !important;
    background: #6b0000 !important;
}
.ui-state-active, .ui-widget-content .ui-state-active {
    border: 1px solid #ffde00 !important;
    background: #1f1f1f !important;
}
.ui-state-hover, .ui-widget-content .ui-state-hover {
    border: 1px solid #fbfbfb !important;
    background: #1f1f1f !important;
}
