sections.code:
:root {
    --main-color: #00adee;
    --main-background: #111;
    --second-background: #181818;
    --hover-background: #232323;
    --main-text: #eff0f1;
    --dimmer-text: #ccc;
    --editor-background: #181818;
    --select-background: #232323;
    --shadow: 0 1px 0.5px rgba(0,0,0,0.3);
    --even-entry-background: rgba(0,0,0,0.6);
    --font-size: 14px;
/* Cursor width and color */
    --cursor-width: 1px;
    --cursor-color: #00adee;
    --sidebar_width: 280px;
/* These are must have static colors */
/* Disable all color/warning Breeze Dark */
    --red: #da4453;
    --yellow: #fdbc4b;
    --warning-disable-all: var(--red);
/* No match regex color from Breeze Dark */
/* added for easy change with swicher */
    --no-match: var(--yellow);
  }
  html {
    text-rendering: optimizeLegibility !important;
    -webkit-font-smoothing: antialiased !important;
  }
  body,
  #options-title {
    border-color: var(--hover-background);
  }
  html.newUI .entry.even,
  .keymap-list > tbody > tr:nth-child(even),
  #stylus-manage [id*="style-"].entry.even {
    background-color: var(--even-entry-background) !important;
  }
  #hotkey-info mark {
    background: var(--main-background) !important;
    border: 1px solid var(--main-color) !important;
    color: var(--main-text) !important;
  }
  #hotkey-info[data-active],
  #hotkey-info > div {
    background: var(--main-background) !important;
    border: 1px solid var(--second-background) !important;
  }
  #stylus-edit,
  body,
  html,
  #notes,
  #header,
  #actions {
    background-color: var(--main-background) !important;
    color: var(--dimmer-text) !important;
  }
  a,
  a:visited,
  .style-name-link {
    color: var(--main-color) !important;
    opacity: 0.9;
  }
  a:hover,
  .style-name-link:hover,
  a:focus-within,
  .style-name-link:focus-within,
  h2:focus-within .style-name-link,
  .entry:hover .style-name-link,
  .style-name-link a.delete:hover,
  .style-name-link a.homepage:hover {
    opacity: 1;
    filter: brightness(115%) !important;
  }
  .disabled h2::after,
  .search-result,
  .search-result-meta,
  .entry.usercss .style-name-link::after {
    background-color: var(--hover-background) !important;
    box-shadow: var(--shadow) !important;
  }
  .entry.usercss .style-name-link::after {
    color: var(--main-color) !important;
  }
  .disabled h2:focus-within .style-name-link,
  .entry.disabled:hover .style-name-link {
    opacity: 0.8;
  }
  #filters label:hover,
  #filters .filter-selection:hover,
  #filters label:hover select,
  #filters .filter-selection:hover select,
  #filters .filter-selection:focus-within select {
    background-color: transparent !important;
    color: var(--main-color) !important;
  }
  #filters label:hover .svg-icon.select-arrow,
  #filters .filter-selection:hover .svg-icon.select-arrow {
    fill: var(--main-color) !important;
  }
  select,
  .svg-icon.select-arrow {
    transition: none !important;
  }
  .entry {
    border-color: var(--second-background) !important;
    box-shadow: inset 0 1px 0 0 var(--second-background) !important;
  }
  .newUI .entry .style-name:hover::before {
    filter: grayscale(1);
  }
  #header {
    width: var(--sidebar_width) !important;
  }
  #header {
    border-right: 1px solid var(--hover-background) !important;
  }
  #header h1 {
    color: var(--main-text) !important;
  }
  #header summary:hover h2,
