@-moz-document domain("rutor.info"), domain("rutor.is"), domain("mirror-rutor.org"), domain("zerkalo-rutor.org"), domain("ru-free-tor.org"), domain("open-tor.org"), domain("fast-bit.org"), domain("ru-tor.net"), domain("fast-tor.net"), domain("proxy-rutor.org"), domain("new-tor.org"), domain("free-ru.org"), domain("rus-tor.com"), domain("6tor.org") {
html, body {
    font-size: 12.5 !important;
    background: #2e2e2e !important;
    max-width: 1100px !important;
    width: 95%;
    margin-left: auto !important;
    margin-right: auto !important;
}
#all {
    margin: 4px !important;
}
.news_date {
    background-color: #464646 !important;
}
a {
    color: #fedd00 !important;
}
