	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	:focus
	{
		outline-color: var(--main-color) !important;
	}
	/*login*/
	.btn-auth, input#login-existing, input#password-existing, input[type="submit"]
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	.btn-auth:hover, input[type="submit"]:hover
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	input#remember-normal
	{
		filter: invert(100%);
	}
	a.btn-auth:visited
	{
		color: var(--dimmer-text) !important;
	}

	/*Edit style page*/
	input:not([class*="color"]), textarea
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	input:active, textarea:active, input:focus, textarea:focus
	{
        	border-color: var(--main-color) !important;
	}
	input[id*="option-default"]
	{
        	border: 1px solid transparent !important;
		box-shadow: var(--shadow) !important
	}
	.edit-setting
	{
		background-color: var(--hover-background) !important;
	}
	/*If your browser shows buttons white because of a default white theme, uncomment this*/
	/*
	input[type="radio"], input#enable-source-editor-code
	{
		filter: invert(100%);
	}
	*/

	/*Edit css*/
	.CodeMirror
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.CodeMirror-linenumber, .CodeMirror-gutters
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.errorExplanation
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}

	/*Code text*/
	.CodeMirror div.CodeMirror-cursor { border-color: var(--main-color); }
	.cm-s-default.CodeMirror { background: var(--second-background); color: var(--main-text); }
	.cm-s-default div.CodeMirror-selected { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: var(--hover-background); }
