/* ==UserStyle==
@name           Undistractify StackOverflow
@namespace      USO Archive
@author         hardy tom
@description    `Remove all the distracting stuff (other sites, chat rooms, meta posts) from your vision, focusing in on resolving your technical problems first.`
@version        20150220.17.55
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("superuser.com"),domain("stackoverflow.com") {
  #hot-network-questions, /* not related to your work */
  .module.community-bulletin, /* never mind what's up on Meta */
  #chat-feature, /* no wasting your time in a semi-related chatroom */
  #footer-sites /* seriously, stop lookin gfor ways to waste your time */
  {
    display:none;
  }
}

/* ==UserStyle==
@name        Dark svtv.org userstyle
@namespace   Mayurifag
@version     2.3.0
@homepageURL https://github.com/Mayurifag/dark-svtv-org-userstyle
@updateURL   https://raw.githubusercontent.com/Mayurifag/dark-svtv-org-userstyle/main/dark-svtv-org.user.css
@license     MIT
@author      Mayurifag
@preprocessor uso
==/UserStyle== */
@-moz-document domain("svtv.org") {
  :root {
    --background-primary: #000;
    --background-secondary: #1e1c1c;
    --background-3: #282828;
    --text-primary: #fff;
    --text-secondary: #939393;
    --accent: #fed27a;
    --thread: #f7931a;
    --factchecking: #ff4a4a;
    --test: #bcaeff;
    --opinions: #7aefff;
    --translation: #2e8eff;
    --my-news-minimal-background: #804c00;
  }
  html {
    background-color: var(--background-primary);
  }
  .page__content-wrap {
    background-color: var(--background-primary);
  }
  .card-news__title {
    color: var(--text-primary);
    background-color: inherit;
  }
  .block-cards__button {
    background-color: var(--accent);
    color: var(--background-secondary);
  }
  .post-fullpage__title {
    color: var(--text-primary);
  }
  .block-cards__title {
    color: var(--text-primary);
  }
  .card-news__category {
    color: var(--accent);
    background-color: inherit;
  }
  .card-news--minimal .card-news__category {
    color: var(--accent);
  }
  .card-news--minimal {
    background-color: var(--my-news-minimal-background) !important;
    border-color: var(--my-news-minimal-background) !important;
  }
  .card-news__content {
    background-color: var(--background-secondary);
  }
  .card-news--minimal .card-news__content:hover {
    color: var(--my-news-minimal-background);
    background-color: var(--my-news-minimal-background) !important;
    border-color: var(--my-news-minimal-background) !important;
  }
  .card-thread__content {
    background-color: var(--background-secondary);
  }
  .card-thread__category {
    color: var(--thread);
    background-color: inherit;
  }
  .post-fullpage__title-info {
    background-color: var(--background-secondary);
  }
  .thread-blob {
    background-color: var(--background-3);
  }
  .thread-blob__date {
    background-color: var(--background-3);
    color: var(--accent);
    font-weight: 400;
    opacity: .9;
  }
  .card-opinions__text {
    background-color: var(--background-3);
  }
  .card-opinions__category {
    background-color: var(--background-3);
    color: var(--opinions);
  }
  .card-opinions__person-name {
    background-color: var(--background-3);
  }
  .card-opinions__date {
    background-color: var(--background-3);
  }
  .card-factchecking__text {
    background-color: var(--background-3);
  }
  .card-factchecking__person {
    background-color: var(--background-3);
  }
  .card-factchecking__person-name {
    background-color: var(--background-3);
  }
  .card-factchecking__category {
    background-color: var(--background-3);
    color: var(--factchecking);
  }
  .card-factchecking__person-description {
    background-color: var(--background-3);
    color: var(--text-secondary);
  }
  .card-factchecking__date {
    background-color: var(--background-3);
  }
  .card-tests__title {
    background-color: inherit;
  }
  .card-tests__category {
    color: var(--test);
    background-color: inherit;
  }
  .block-cards__item-width-1-5 {
    color: var(--background-3);
    background-color: inherit;
  }
  .card-thread__title {
    background-color: inherit;
    color: var(--text-primary);
  }
  .banner__navigation-text {
    background-color: inherit;
  }
  .banner__navigation-date {
    background-color: inherit;
  }
  .sb-ad-banner {
    display: none;
  }
  .sb-footer__text {
    background-color: var(--background-secondary);
    color: var(--text-primary);
  }
  .sb-footer {
    background-color: var(--background-secondary);
    color: var(--text-primary);
    border-color: var(--background-secondary) !important;
  }
  .card-news {
    background-color: inherit;
    border-color: var(--background-secondary);
  }
  .card-news__date {
    color: var(--text-secondary);
  }
  .page__footer-wrap {
    box-shadow: inherit;
  }
  .post-donation__header {
    background-color: var(--background-secondary);
  }
  .post-donation__crypto-name {
    background-color: var(--background-secondary);
  }
  .card-thread__date {
    color: var(--text-secondary);
  }
  .page--is-test .page__footer-wrap {
    display: none;
  }
  .quiz-test__title {
    color: var(--text-primary);
  }
  .quiz-test__poll-value-text {
    color: var(--text-primary);
  }
  .quiz-test__poll-value {
    background-color: var(--background-3);
    color: var(--text-primary);
  }
  .quiz-test__socials-header {
    color: var(--text-primary);
  }
  .post-fullpage__content p {
    background-color: var(--background-secondary);
    color: var(--text-primary);
  }
  .post-fullpage__content {
    background-color: var(--background-secondary) !important;
    color: var(--text-primary);
  }
  .author-header__name {
    color: var(--text-primary);
  }
  .author-header__description {
    color: var(--text-secondary);
  }
  /* TODO: make this optional for userstyles
  .card-news--minimal .card-news__image-wrap {
    display: inherit;
  }
  */
  /* TODO: think do I need to recolor watch live stream button
  .sb-live__button {
    background-color: #e0e0e0;
  } */
  .thread-blob p {
    background-color: var(--background-3);
  }
  .thread-blob::after {
    background-color: var(--background-3);
  }
  /* 404 page */
  .container__title {
    color: var(--text-primary);
  }
  .quiz-test__category {
    color: var(--test) !important;
  }
  /* some pages have no donation footer actually, but have wrong footer div */
  .author-template .page__footer-wrap {
    background-color: inherit;
  }
  .page--is-post-donation .page__footer-wrap {
    background-color: inherit;
  }
  /* Sidebar navigation links colored on hover */
  a.sb-navigation__item-link[href*="/news/"]:hover .sb-navigation__icon svg {
    fill: var(--accent);
  }
  a.sb-navigation__item-link[href*="/opinion/"]:hover .sb-navigation__icon svg {
    fill: var(--opinions);
  }
  a.sb-navigation__item-link[href*="/thread/"]:hover .sb-navigation__icon svg {
    fill: var(--thread);
  }
  a.sb-navigation__item-link[href*="/factchecking/"]:hover .sb-navigation__icon svg {
    fill: var(--factchecking);
  }
  a.sb-navigation__item-link[href*="/tests/"]:hover .sb-navigation__icon svg {
    fill: var(--test);
  }
  a.sb-navigation__item-link[href*="/translation/"]:hover .sb-navigation__icon svg {
    fill: var(--translation);
  }
  a.sb-navigation__item-link[href*="/search/"]:hover .sb-navigation__icon svg {
    fill: var(--text-primary);
  }
  a.sb-navigation__item-link[href*="/context/"]:hover .sb-navigation__icon svg {
    fill: var(--accent);
  }
  .post-card-notice--tor .post-card-notice__description {
    background-color: inherit !important;
  }
  .post-card-notice--red-shield {
    background-color: var(--background-3);
  }
  .post-card-notice--red-shield .post-card-notice__description {
    background-color: var(--background-3);
  }
  .sb-currency__items {
    background-color: var(--background-secondary);
  }
  .sb-currency {
    background-color: var(--background-secondary);
    border-color: var(--background-secondary) !important;
  }
  .sb-currency__value {
    color: var(--text-primary);
  }
  .sb-currency__token {
    color: var(--text-primary);
  }
  /* xd nice color */
  .sb-currency__token--btc {
    color: var(--thread);
    opacity: 1;
  }
  .sb-currency__arrow {
    vertical-align: inherit;
  }
  .post-jobs-item {
    background-color: var(--background-3);
  }
  .post-jobs-item__paragraph {
    background-color: var(--background-3) !important;
  }
  /* 'Online' sidebar page and block */
  .tag-online .page__content-wrap {
    background: inherit;
  }
  .online__message-text {
    background: var(--background-secondary);
    color: var(--text-primary);
  }
  .block-cards__item {
    background: var(--background-secondary) !important;
  }
  .online__message-time {
    color: var(--text-secondary);
  }
  .sb-online {
    background: var(--background-secondary) !important;
    color: var(--text-primary);
    border-color: var(--background-secondary) !important;
  }
  .sb-online__show-all-button {
    background-color: var(--background-3);
    color: var(--thread)
  }
  .sb-online__show-all-button:hover {
    background-color: var(--background-4);
  }
  .sb-online__item {
    background: var(--background-secondary);
    color: var(--text-primary);
  }
  .sb-online__item:hover {
    background: var(--background-4);
  }
  .post-fullpage__content.page-gas {
    background-color: transparent !important;
  }
  /* That looks stupid. I should make this semi-transparent or something */
  .page-gas__social-list {
    background: var(--background-secondary);
  }
  .page-gas__social {
    background: var(--background-secondary);
  }
  .page-gas__social-text {
    color: var(--text-secondary);
  }
}

