/*Fandom/Wikia is a god awful place. Please use an adblocker if you must use it.*/
/*This script only cleans up actual page jargin, not advertisements. That's a job for your Adblocker.*/

/*unf&$k the navbar*/
.wds-global-navigation {
	position: absolute;
	box-shadow: 0 0 0 0;
    background: none;
	border-top: 0 solid transparent;
	border-image: none;
}
.wds-global-navigation__search {
	width: 100%;
}
.wds-global-navigation__logo,
.wds-global-navigation a[href="//fandom.wikia.com/topics/games"],
.wds-global-navigation a[href="//fandom.wikia.com/topics/movies"],
.wds-global-navigation a[href="//fandom.wikia.com/topics/tv"],
.wds-global-navigation a[href="//fandom.wikia.com/video"],
.wds-global-navigation__link-group,
.wds-global-navigation__wikis-menu,
.wds-global-navigation__start-a-wiki {
	display: none;
}
.wds-global-navigation__dropdown-controls {
	background-color: #002a32;
}

/*hide stupid wikia bar on the bottom of the page*/
.WikiaBarWrapper, .WikiaBarCollapseWrapper {
	display: none;
}

/*hide absurdly huge footer section on every page*/
.wds-global-footer {
	display: none;
}

/*hide "Also on Fandom!" Bar*/
.WikiaFooter, .WikiaBarWrapper section {
	display: none;
}

/*Hide "wikia rail" on article pages*/
.WikiaRail {
	display: none;
}
.WikiaMainContent {
	width: 100%;
}

sourceCode:
/* ==UserStyle==
@name           Wikia/Fandom - Uncluttered and Unbranded
@namespace      USO Archive
@author         CheatFreak47
@description    `A userstyle with minimal hardcore changes to the Wikia Site, only removing stuff I and many others would consider clutter, junk, crap... You get the idea.I found that trying to use Wikia recently has felt like a massive chore and that's a clear cut problem, because it houses a lot of valuable information about all sorts of niche subjects.The focus of this style is to make each Wiki more about the actual wiki you are on, and less about Wikia's recent change of direction toward "Fandom".Note: This <b>does not remove advertisements.</b> That is a job for an Adblocker, not CSS.`
@version        20181213.5.46
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("fandom.com"), domain("wikia.com") {
/*Fandom/Wikia is a god awful place. Please use an adblocker if you must use it.*/
/*This script only cleans up actual page jargin, not advertisements. That's a job for your Adblocker.*/

/*unf&$k the navbar*/
.wds-global-navigation {
	position: absolute;
	box-shadow: 0 0 0 0;
    background: none;
	border-top: 0 solid transparent;
	border-image: none;
}
.wds-global-navigation__search {
	width: 100%;
}
.wds-global-navigation__logo,
.wds-global-navigation a[href="//fandom.wikia.com/topics/games"],
.wds-global-navigation a[href="//fandom.wikia.com/topics/movies"],
.wds-global-navigation a[href="//fandom.wikia.com/topics/tv"],
.wds-global-navigation a[href="//fandom.wikia.com/video"],
.wds-global-navigation__link-group,
.wds-global-navigation__wikis-menu,
.wds-global-navigation__start-a-wiki {
	display: none;
}
.wds-global-navigation__dropdown-controls {
	background-color: #002a32;
}

/*hide stupid wikia bar on the bottom of the page*/
.WikiaBarWrapper, .WikiaBarCollapseWrapper {
	display: none;
}

/*hide absurdly huge footer section on every page*/
.wds-global-footer {
	display: none;
}

/*hide "Also on Fandom!" Bar*/
.WikiaFooter, .WikiaBarWrapper section {
	display: none;
}

/*Hide "wikia rail" on article pages*/
.WikiaRail {
	display: none;
}
.WikiaMainContent {
	width: 100%;
}
}

