--theme_scrollactive: hsl(var(--clr),var(--clr2),55%);
--theme_default_bg: hsl(var(--clr),var(--clr2),15%);
--theme_default_postbg: hsl(var(--clr),var(--clr2),20%);
--theme_default_postbg2: hsl(var(--clr),var(--clr2),25%);
--theme_default_postbg3: hsl(var(--clr),var(--clr2),25%);
--theme_default_glassbg: hsl(var(--clr),var(--clr2),20%,.7);
--theme_default_glassbg2: hsl(var(--clr),var(--clr2),20%,.9);
--theme_default_borderhighlight: var(--theme_default_link);
--theme_default_shadowhighlight: hsl(var(--clr_link),var(--clr_link2),60%,.6);
--theme_default_postbghighlight: hsl(var(--clr_link),calc(var(--clr_link2)/3),20%);
--theme_default_mod: hsl(var(--clr_link),var(--clr_link2),70%);
--theme_default_spectext: hsl(290,30%,60%);
--theme_default_quote: hsl(var(--clr_num),60%);
--theme_default_quote2: hsl(var(--clr_num),65%);
--theme_default_quote-bg: hsl(var(--clr_num),60%,.1);
--theme_default_border: hsl(var(--clr),var(--clr2),100%,.1);
--theme_default_altborder: hsl(var(--clr),var(--clr2),100%,.15);
--theme_default_text: hsl(var(--clr),var(--clr2),85%);
--theme_default_alttext: hsl(var(--clr),var(--clr2),70%);
--theme_default_alttext2: hsl(var(--clr),var(--clr2),50%);
--theme_default_contrasttext: hsl(var(--clr),var(--clr2),0%);
--theme_red: hsl(0,100%,65%);
--theme_yellow: hsl(45,90%,70%);
--theme_green: hsl(120,60%,60%);
--theme_sage: hsl(0,100%,70%);
--theme_ophui: hsl(var(--clr_num),60%);
--theme_default_link: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 60%));
--theme_default_hover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 70%));
--theme_default_altlink: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 60%),.8);
--theme_default_althover: hsl(var(--clr_link),var(--clr_link2),calc(var(--clr_link3) + 70%),.8);
--theme_default_title: hsl(var(--clr_link),var(--clr_link2),85%);
--theme_default_drag: hsl(var(--clr),var(--clr2),100%,.05);
--theme_default_spoiler: hsl(var(--clr),var(--clr2),30%);
--theme_default_icon: hsl(var(--clr),var(--clr2),65%);
--theme_default_btntext: var(--theme_default_text);
--theme_default_altbtntext: hsl(var(--clr),var(--clr2),15%);
--theme_default_btnbg: hsl(var(--clr),var(--clr2),20%);
--theme_default_btnborder: hsl(var(--clr),var(--clr2),100%,.1);
--theme_default_altbtnbg: hsl(var(--clr),var(--clr2),17%);
--theme_default_btnbghover: hsl(var(--clr),var(--clr2),25%);
--theme_default_altbtnbghover: hsl(var(--clr),var(--clr2),20%);
--theme_default_inputtext: var(--theme_default_text);
--theme_logo_text: var(--theme_default_link);
--theme_select: 100% -13px;
--theme_checkbox: 1px -18px;
--theme_nmswitch: var(--theme_default_link);
--theme_mpborder: hsl(var(--clr),var(--clr2),35%);
--theme_mubtn: transparent;
--theme_sbthumb: var(--theme_scroll);
--theme_sbtrack: var(--theme_scrollbg);
--theme_invert: unset;
--theme_shadow: hsl(var(--clr),var(--clr2),2%,.15);
--theme_shadow2: hsl(var(--clr),var(--clr2),2%,.2);
--theme_shadow3: hsl(var(--clr),var(--clr2),2%,.25);
--theme_shadow4: hsl(var(--clr),var(--clr2),2%,.35);
--theme_blockbg: var(--theme_default_postbg);
--theme_border: var(--theme_default_border);
}

@media only screen and (min-width: 480px) {
::-webkit-scrollbar {background: var(--theme_scrollbg); height: 14px; width: 14px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: var(--theme_scroll); background-clip: padding-box; border: 2px solid transparent; border-radius: 4px;}
::-webkit-scrollbar-thumb:hover {background-color: var(--theme_scrollhover);}
::-webkit-scrollbar-thumb:active {background-color: var(--theme_scrollactive);}
::-webkit-scrollbar-corner {background: var(--theme_scrollbg);}
}

.loader, .loader:before, .loader:after {color: var(--theme_default_link);}

div[style="width: 100%; height: 100%; position: fixed; top: 0px; left: 0px; z-index: 2000000000; background-color: rgb(255, 255, 255); opacity: 0.5;"] {background-color: var(--theme_default_bg) !important;}

.cntnt {max-width: 1024px; margin: 0 auto; padding: 0 5px 0 42px;}
.ctlg, .body-stickers {max-width: 1024px; margin: 0 auto; padding: 0 10px;}
@media only screen and (min-width: 1280px) and (orientation:portrait),  only screen and (min-width: 1280px) and (orientation:landscape){
.cntnt {max-width: 1024px; margin: 0 auto; padding: 0 5px 0 42px;}
.ctlg, .body-stickers {max-width: 1024px; margin: 0 auto; padding: 0 10px;}
}
@media only screen and (min-width: 1480px) and (orientation:portrait),  only screen and (min-width: 1480px) and (orientation:landscape){
.cntnt {max-width: 1280px; margin: 0 auto; padding: 0 5px 0 42px;}
.ctlg, .body-stickers {max-width: 1280px; margin: 0 auto; padding: 0 10px;}
}
@media only screen and (min-width: 1920px) and (orientation:portrait),  only screen and (min-width: 1920px) and (orientation:landscape){
.cntnt {max-width: 1480px; margin: 0 auto; padding: 0 5px 0 42px;}
.ctlg, .body-stickers {max-width: 1480px; margin: 0 auto; padding: 0 10px;}
}

*::selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-moz-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-webkit-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}

.rmenu, #plashque, #mobile-plashque, .logo img, .logo br, .mmm {display: none;}

a {color: var(--theme_default_link); text-decoration: none; transition: color .1s; outline: none;}
a:hover {color: var(--theme_default_hover);}
a:link {text-decoration: none;}
a[href*="youtube.com"] {border-bottom: none;}
a[href$="sage"], a[href$="Sage"], a[href$="SAGE"] {color: var(--theme_sage);}
a[savefrom_lm_is_link="1"] {background: none !important;}
a[savefrom_lm_is_link="1"]::before {content: 'save_alt'; font: 16px/1 'Material Icons', sans-serif;}
.hashlink {color: var(--theme_default_altlink);}
.hashlink:hover {color: var(--theme_default_althover);}

/* ===== Main Page ===== */
.st0, .st1, use[fill="url(#gradientaaCw2bI38)"] {fill: var(--theme_default_link);}
.welcomefriend {color: var(--theme_default_link);}

.wraper.mainpage {margin: 0 10px;}
.mcontent, .board-data {max-width: 1000px; width: auto;}
.hdescription, .hdescription-stats {font-family: inherit;}
.postscontainer {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}
font[color="red"], font[color="#ff0000"], span[style="font-size:10px; color:red;"], span[style="color: #ff0000;"], .closed-board, .archive-thread, .thanks-abu {color: var(--theme_red) !important;}

.mainpage .pager {box-shadow: none;}
.hnews .news-posts-count {color: var(--theme_default_alttext2);}

/* Stats */
.ui-widget, .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {font-family: inherit;}

.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited, a.ui-button, a:link.ui-button, a:visited.ui-button, .ui-button {color: var(--theme_default_alttext);}
.ui-state-hover a, .ui-state-hover a:hover, .ui-state-hover a:link, .ui-state-hover a:visited, .ui-state-focus a, .ui-state-focus a:hover, .ui-state-focus a:link, .ui-state-focus a:visited, a.ui-button:hover, a.ui-button:focus {color: var(--theme_default_text);}

#tabbed li {border: none; border-bottom: 2px solid transparent; border-radius: 0; font-weight: bold;}
#tabbed li.ui-tabs-active {background: none; border-bottom-color: var(--theme_default_link); color: var(--theme_default_link); margin-bottom: unset; padding-bottom: unset;}

.ui-widget-content {background: var(--theme_default_postbg);}
.ui-widget.ui-widget-content {box-shadow: 0 0 0 1px var(--theme_default_border);}
.ui-widget-header {background: var(--theme_default_postbg); padding: 4px;}
.tracker-tab, .board-data {border: none;}
.cloudheader .cloudoption {color: var(--theme_default_alttext);}
.cloudheader .cloudoption:hover {color: var(--theme_default_text);}

.table .thead .th {background: var(--theme_default_link); border: none; color: var(--theme_default_altbtntext);}
.table .thead .th a[style="color:black;"] {color: var(--theme_default_altbtntext) !important;}
.board-speed {color: var(--theme_default_alttext);}

hr {border-color: transparent;}
.hr {border-top-color: var(--theme_default_border);}
.box-data .data {border-color: var(--theme_default_border);}

/* FAQ */
a.logo {background: none; color: var(--theme_default_link) !important; padding: 0;}
a.logo::before {content: "flash_on"; display: inline-block; font: 80px/1 'Material Icons', sans-serif; vertical-align: -25px; width: 70px;}

.box {background: var(--theme_default_postbg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}
.box hr {border-top-color: var(--theme_default_border);}
.boardname {color: var(--theme_default_link);}

/* Donate Abu */
#adminbar ul.menu ul li a {color: var(--theme_default_link);}
#adminbar ul.menu ul li a:hover {color: var(--theme_default_hover);}
#adminbar ul.menu ul li a:visited {color: var(--theme_default_link);}
.logo {color: var(--theme_default_link);}

.reply {background: var(--theme_default_postbg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}

/* Passcode kupi - Abu pomogi */
.header {padding: 10px 0 5px 0; margin-bottom: 0;}

#fixed {background-color: var(--theme_default_postbg); border-top: 1px solid var(--theme_default_border); border-bottom: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.amenu {font-family: inherit !important;}
.topmenu li a.mitem:link, .topmenu li a.mitem:visited {background: var(--theme_default_btnbg); border: 1px solid var(--theme_default_border); border-radius: 3px; color: var(--theme_default_text);}
.topmenu li a.mitem:hover {background: var(--theme_default_btnbghover);}
.addban, .floatforms {background-color: var(--theme_default_postbg); border: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.floatforms {font-family: inherit;}

/* Stickers */
.body-stickers .sticker {background-color: var(--theme_default_postbg); border-color: var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow); color: var(--theme_default_text);}
.body-stickers .sticker:hover {border-color: var(--theme_default_altborder);}

.sticker {scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}

/* ===== Catalog ===== */
.box-header {font-family: unset; font-size: 1em;}
.pagetitle {color: var(--theme_default_text);}

.linktoboard {display: inline-block; float: unset; padding-top: 0; margin-right: 8px; vertical-align: -2px;}
.linktoboard a {color: var(--theme_default_alttext); font-size: 0;}
.linktoboard a:hover {color: var(--theme_default_link);}
.linktoboard a::before {content: "arrow_back"; font: 24px/1 'Material Icons', sans-serif; padding: 2px; margin: -2px; vertical-align: -6px;}

.ctlg {margin: 0 auto 10px;}
.ctlg__thread {border: none; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow); margin: 5px;}
.ctlg__thread_abs {box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_shadow3); margin: 0; margin-top: -20px; margin-left: -70px;}
.ctlg__img {padding: 10px 10px 0 10px;}
.ctlg__img img {border-radius: 3px; height: auto; width: auto;}
.ctlg__meta {background-color: transparent; font-family: unset; color: var(--theme_default_alttext2);}
.ctlg__title {font-family: unset; color: var(--theme_default_text);}
.orange {color: var(--theme_default_link);}
