    outline: 1px solid var(--main-color) !important;
  }
  #stylus-popup #confirm div {
    border-color: var(--red) !important;
  }
  #sections > div:not(:first-of-type) {
    border-top: 2px solid var(--second-background) !important;
  }
  button,
  .remove-applies-to,
  .add-applies-to,
  #backup-buttons .dropdown-content a {
    transition: color 0.25s, border-color 0.25s;
    transition: background-color 0.25s, border-color 0.25s;
  }
  #save-button,
  #beautify,
  #cancel-button,
  #from-mozilla,
  #to-mozilla,
  #editor.keyMap,
  #editor.theme,
  #editor.matchHighlight,
  .applies-type.style-contributor,
  .add-section,
  .beautify-section,
  .test-regexp,
  .remove-applies-to,
  .add-applies-to,
  #check-all-updates,
  #add-style-label,
  #manage-options-button,
