#adminbar ul.menu ul li a:hover {color: var(--theme_default_hover);}
#adminbar ul.menu ul li a:visited {color: var(--theme_default_link);}
.logo {color: var(--theme_default_link);}

.reply {background: var(--theme_default_postbg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow);}

/* Passcode kupi - Abu pomogi */
.header {padding: 10px 0 5px 0; margin-bottom: 0;}

#fixed {background-color: var(--theme_default_postbg); border-top: 1px solid var(--theme_default_border); border-bottom: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.amenu {font-family: inherit !important;}
.topmenu li a.mitem:link, .topmenu li a.mitem:visited {background: var(--theme_default_btnbg); border: 1px solid var(--theme_default_border); border-radius: 3px; color: var(--theme_default_text);}
.topmenu li a.mitem:hover {background: var(--theme_default_btnbghover);}
.addban, .floatforms {background-color: var(--theme_default_postbg); border: 1px solid var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow);}
.floatforms {font-family: inherit;}

/* Stickers */
.body-stickers .sticker {background-color: var(--theme_default_postbg); border-color: var(--theme_default_border); box-shadow: 0 1px 3px var(--theme_shadow); color: var(--theme_default_text);}
.body-stickers .sticker:hover {border-color: var(--theme_default_altborder);}

.sticker {scrollbar-color: var(--theme_scroll) transparent; scrollbar-width: thin;}

/* ===== Catalog ===== */
.box-header {font-family: unset; font-size: 1em;}
.pagetitle {color: var(--theme_default_text);}

.linktoboard {display: inline-block; float: unset; padding-top: 0; margin-right: 8px; vertical-align: -2px;}
.linktoboard a {color: var(--theme_default_alttext); font-size: 0;}
.linktoboard a:hover {color: var(--theme_default_link);}
.linktoboard a::before {content: "arrow_back"; font: 24px/1 'Material Icons', sans-serif; padding: 2px; margin: -2px; vertical-align: -6px;}

.ctlg {margin: 0 auto 10px;}
.ctlg__thread {border: none; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 3px var(--theme_shadow); margin: 5px;}
.ctlg__thread_abs {box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_shadow3); margin: 0; margin-top: -20px; margin-left: -70px;}
.ctlg__img {padding: 10px 10px 0 10px;}
.ctlg__img img {border-radius: 3px; height: auto; width: auto;}
.ctlg__meta {background-color: transparent; font-family: unset; color: var(--theme_default_alttext2);}
.ctlg__title {font-family: unset; color: var(--theme_default_text);}
.orange {color: var(--theme_default_link);}
