/* ===== Page ===== */
.warningcover {background-color: var(--theme_default_glassbg2); backdrop-filter: blur(10px); opacity: 1;}
.warningbox {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 6px 16px var(--theme_shadow2); margin-top: -50px; overflow: auto;}
.warningboxbutton a {font-weight: bold; text-transform: uppercase; letter-spacing: .5px;}
.warningboxbutton a[id="ageboxallow"] {font-size: 0;}
.warningboxbutton a + br {display: none;}
.warningboxbutton a[id="ageboxallow"]::before {content: "Я согласен"; font-size: 18px; margin-right: 12px;}
.warningboxbutton a[id="ageboxdisallow"] {color: var(--theme_red);}

#fullscreen-container {animation: open .1s ease-out; box-shadow: 0 6px 16px var(--theme_shadow4); padding: 0; margin: 6px 4px 4px 4px;}
#fullscreen-container:hover:after {opacity: 1;}
#fullscreen-container:after {background: var(--theme_default_glassbg); border-radius: 3px; color: var(--theme_default_text); padding: 1px 5px; top: 2px; left: 2px; width: unset; opacity: 0; transition: .1s; white-space: unset;}
#fullscreen-container video {outline: none;}
.fullmedia__meta {background-color: var(--theme_default_postbg); border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2); color: var(--theme_default_text); line-height: 1.4; bottom: -26px; padding: 3px 8px;}

.na {background: var(--theme_default_postbg); border-radius: 0; display: block !important; height: 25%; width: 35px; padding: 0; left: 0; right: unset; text-align: center; z-index: 201;}
.na_type_up:hover, .na_type_up:active {background: linear-gradient(0deg, var(--theme_default_postbg) 0%, var(--theme_default_bg) 90%, var(--theme_default_postbg) 100%);}
.na_type_down:hover, .na_type_down:active {background: linear-gradient(0deg, var(--theme_default_postbg) 0%, var(--theme_default_bg) 10%, var(--theme_default_postbg) 100%);}
.na__icon {display: none}
.na_type_up {top: 25%;}
.na_type_up::before {color: var(--theme_default_alttext2); content: "keyboard_arrow_up"; font: 32px/1 'Material Icons', sans-serif; display: block; position: absolute; left: 2px; top: 10px;}
.na:hover::before {color: var(--theme_default_alttext);}
.na_type_down {bottom: 25%; transform: none;}
.na_type_down::before {color: var(--theme_default_alttext2); content: "keyboard_arrow_down"; font: 32px/1 'Material Icons', sans-serif; display: block; position: absolute; left: 2px; bottom: 10px;}

.alert {background-color: var(--theme_default_glassbg2); border-radius: 3px; box-shadow: 0 0 0 1px var(--theme_default_border), 0 1px 6px var(--theme_shadow2);}

.media-thumbnail img {border-radius: 3px; box-shadow: 0 1px 6px 0 var(--theme_shadow3);}

