div#ws div#sidebar div.sideblock {
    border: 2.5px double #ffde00 !important;
}
#sidebar {
    width: 200px;
    position: relative;
    left: 15px;
    top: 40px;
}
h1 {
    color: #d0d0d0 !important;
}
h2 {
    color: #d0d0d0 !important;
    background-color: #2e2e2e !important;
    background-image: none !important;
}
h2 a {
    color: #ffde00 !important;
}
h3 a {
    padding: 3px !important;
    color: #c6c6c6 !important;
}
h3 a:hover {
    background-color: #e39300 !important;
    color: white !important;
}
td {
    color: #d6d6d6 !important;
}
tr.backgr td {
    color: #cac8c8 !important;
}
div#index tr a {
    color: #d0d0d0 !important;
    font-size: 12.5 !important;
}
div#index tr a:hover {
    color: #ffe018 !important;
}
div#index tr.tum {
    background-color: #3a3a3a !important;
}
div#index tr:hover {
    background-color: #515151 !important;
}
div#index tr.tum:hover {
    background-color: #515151 !important;
}
.green {
    color: #00b500 !important;
}
.red {
    color: #e95353 !important;
}
#download {
    border: solid 1px #6b6b6b !important;
    background-color: #464646 !important;
    font-size: 22px !important;
}
.hidewrap {
    border: 1px solid #6b6b6b !important;
}
.hidehead {
    background-color: #595858 !important;
    color: #d0d0d0 !important;
}
.hidebody {
    background-color: #3c3c3c !important;
    border-top: 1px solid #6b6b6b !important;
    color: #b7b7b7 !important;
}
strong, b, span {
    color: #d0d0d0 !important;
}
.fake_link {
    background-color: #595858 !important;
}
.d_small {
    font-size: 11px !important;
    color: #d6d6d6 !important;
}
table#files td {
    border: 1px dashed #7c7c7c !important;
}
fieldset {
    border: 1px solid #7a7a7a !important;
}
legend {
    color: #d0d0d0 !important;
}
.c_h {
    background-color: #404040 !important;
}
.c_t {
    background-color: #3a3a3a !important;
    border: 1px solid #464646 !important;
}
.c_h_a {
    background-color: #2a1919 !important;
}
.c_t_a {
    background-color: #1f0e0e !important;
}
tr.backgr {
    background-color: #3a3a3a !important;
    background-image: none !important;
    background-repeat: repeat-x !important;
}
.u-block {
    background: #696969 !important;
    border: solid 1px #696969 !important;
}
#rules h2:hover, #preview h2:hover, h2.hover {
    background-color: #2e2e2e !important;
}
div.u_space {
    background: #3e3e3e !important;
    border: solid 0px #fafafa !important;
}
p, ul {
    color: #d6d6d6 !important;
}
#dropdeck th {
    color: azure !important;
}
.opts a {
    border-bottom: solid 1px #fedd00 !important;
    background: none !important;
}
.opts a:hover {
    background-color: #3c0000 !important;
    border-color: #270000 !important;
    color: #fedd00 !important;
}
#preview > div {
    background-color: #2e2e2e !important;
    color: #d0d0d0 !important;
}
